this["JST"]["src/js/templates/entrusted.html"] = function(obj) {
obj || (obj = {});
var __t, __p = '', __e = _.escape;
with (obj) {
__p += '<a href="/" id="logo-mobile"><img src="/img/logo-green-small.png" alt="CrossRoads Church Norfolk"></a>\n<section>\n    <img src="/img/entrusted-page-banner.png" style="width: 100%; max-width: 400px; border: 0;">\n    <br><br>\n    <h2>November 18 - 19, 2016</h2>\n    <h3>Register Now!</h3>\n    <p>\n        Use this link to register for the conference: <a href="https://crcnorfolk.ccbchurch.com/form_response.php?id=119" target="_blank">https://crcnorfolk.ccbchurch.com/form_response.php?id=119</a>.\n    </p>\n    <p>&nbsp;</p>\n    <h3>Location</h3>\n    <p>\n        Hosted by CrossRoads Church:<br>\n        <a href="http://maps.google.com/?q=1127%Surrey%Crescent%Norfolk%VA%23508" target="_blank">1127 Surrey Crescent, Norfolk VA</a>\n    </p>\n</section>\n<section class="alternate">\n    <h3>Is the Entrusted Conference for Me?</h3>\n    <ul>\n        <li>I\'m curious about fostering or adopting</li>\n        <li>I\'m actively pursuing fostering or adoption</li>\n        <li>I\'ve been matched with a waiting child</li>\n        <li>I\'m a foster parent and/or have already adopted</li>\n        <li>My friend/family has adopted and I want to better understand their journey</li>\n    </ul>\n</section>\n<section>\n    <h3>Cost</h3>\n    <p>\n        $30/individual<br>\n        $50/couple\n    </p>\n    <p>&nbsp;</p>\n    <h3>Main Session Topics:</h3>\n    <ul>\n        <li>THE BIBLE AND ADOPTION: why me?</li>\n        <li>A FAMILY UNITED: when adoption is our mission</li>\n        <li>CONFLICTED IDENTITY: your past doesn\'t define you, but it\'s a part of who you are</li>\n    </ul>\n    <p>&nbsp;</p>\n    <h3>Breakout Sessions:</h3>\n    <ul>\n        <li>Domestic Infant Adoption</li>\n        <li>Foster Care/Adoption from Foster Care</li>\n        <li>International Adoption</li>\n        <li>Where Do I Start?</li>\n        <li>Financing/Fundraising Ideas</li>\n        <li>Navigating Open Adoption/Birth Families</li>\n        <li>Special Needs Adoption</li>\n        <li>When My Spouse Feels Called, but I\'m Not There Yet</li>\n        <li>Bringing Church Awareness</li>\n        <li>Transracial Adoption</li>\n        <li>Parenting After Adoption</li>\n        <li>When Love Isn\'t Instant</li>\n    </ul>\n    <p>&nbsp;</p>\n    <h3>Schedule</h3>\n    <p><strong>Friday (November 18th)</strong></p>\n    <table>\n        <tr>\n            <td>7 PM</td>\n            <td>Worship and Welcome</td>\n        </tr>\n        <tr>\n            <td>7:30 PM &nbsp;</td>\n            <td>Main Session #1</td>\n        </tr>\n        <tr>\n            <td>8:45 PM</td>\n            <td>Breakout Session #1</td>\n        </tr>\n        <tr>\n            <td>9:30 PM</td>\n            <td>Dismiss</td>\n        </tr>\n    </table>\n    <p><strong>Saturday (November 19th)</strong></p>\n    <table>\n        <tr>\n            <td>9 AM</td>\n            <td>Worship and Welcome</td>\n        </tr>\n        <tr>\n            <td>9:30 AM</td>\n            <td>Breakout Session #2</td>\n        </tr>\n        <tr>\n            <td>10:15 AM &nbsp;</td>\n            <td>Agency Exhibit/shopping</td>\n        </tr>\n        <tr>\n            <td>11 AM</td>\n            <td>Main Session #2</td>\n        </tr>\n        <tr>\n            <td>12 PM</td>\n            <td>Q&amp;A Panel/Lunch</td>\n        </tr>\n        <tr>\n            <td>1:30 PM</td>\n            <td>Breakout Session #3</td>\n        </tr>\n        <tr>\n            <td>2:15 PM</td>\n            <td>Main Session #3</td>\n        </tr>\n        <tr>\n            <td>3:15 PM</td>\n            <td>Dismiss</td>\n        </tr>\n    </table>\n</section>\n<section class="alternate">\n    <h3>Kids Movie Night</h3>\n    <p>\n        There is a movie night option being offered at the church for ages 5+ on Friday night for an additional fee - $5/child or $15/family\n    </p>\n    <p style="font-style: italic;">* You must register for this event when you register for the conference.*</p>\n</section>\n<section>\n    <h3>MAIN SESSION SPEAKERS</h3>\n    <h4>Nick and Kristle Davis</h4>\n    <img src="/img/DavisPhoto.png" style="width: 100%; max-width: 400px; border: 0;">\n    <p>\n        Kristle grew up to drug, alcohol and mentally affected parents. As the abuse in her family escalated, she sought help through the foster care system as a young teenager. Seven foster homes later, her forever family stepped in and dramatically changed her future.\n    </p>\n    <p>\n        Nick, having had an “all American” childhood, met Kristle during their college years and they married in 2004. Nick and Kristle suffered 2 miscarriages before they were blessed with 2 biological children.\n    </p>\n    <p>\n        After 10 years of engaging in a limited way, the Davises accepted their calling to foster care and adoption. They adopted a son in 2016 and use this story of coming full circle to establish church engagement in community outreach.\n    </p>\n    <p>\n        Click <a href="https://vimeo.com/106782841" target="_blank"><strong>HERE</strong></a> to watch a video of Kristle sharing more of her journey through foster care.\n    </p>\n    <p>\n        Use this link to register for the conference: <a href="https://crcnorfolk.ccbchurch.com/form_response.php?id=119" target="_blank">https://crcnorfolk.ccbchurch.com/form_response.php?id=119</a>.\n    </p>\n</section>';

}
return __p
};

